example.code:

var min = 2;
var max = 5;
for (i in 1.to(max)) {
	if (i > min) {
		i.println();
	}
}


// Print the titles of all books
for (b in t_book.all) {
	b.a_title.println();
}

// Print the number of books
t_book.all.size().println();

// Print the total number of pages
t_book.all.collect(b|b.i_pages).
	sum().println();
